from pygame import *
import pygame

a = 'Artem Kovalchyk lox'

window = pygame.display.set_mode((1000, 700))

screen_width = 1200
screen_heigth = 708

class Game_Spaite():
    def __init__(self, x, y, width, height, filename):
        self.rect = pygame.Rect(x, y, width, height)
        self.image = transform.scale(pygame.image.load(filename), (width, height))
    def show(self):
        window.blit(self.image, self.rect)

class Player(Game_Spaite):
    def __init__(self, x, y, width, height, filename):
        self.rect = pygame.Rect(x, y, width, height)
        self.image = transform.scale(pygame.image.load(filename), (width, height))
    def show(self):
        window.blit(self.image, self.rect)

fps = pygame.time.Clock()

player = Player(500, 500, 50, 70, 'player.png')

def draw_grid():
    for line in range(0, 100):
        pygame.draw.line(window, (255, 255, 255), (0, line * tile_size), (screen_width, line * tile_size))
        pygame.draw.line(window, (255, 255, 255), (line * tile_size, 0), (line * tile_size, screen_heigth))

tile_size = 120

class World():
    def __init__(self, data):
        self.tile_list = []
        grass_block_img = pygame.image.load('grass_block.png')
        stone_block_img = pygame.image.load('stone_block.png')
        grass_sloope_img = pygame.image.load('grass_slope.png')
        grass_sloope2_img = pygame.image.load('grass_slope2.png')
        grass_sloope3_img = pygame.image.load('grass_slope3.png')
        row_count = 0
        for row in data:
            col_count = 0
            for tile in row:
                if tile == 1:
                    img = pygame.transform.scale(grass_block_img, (tile_size, tile_size))
                    img_rect = img.get_rect()
                    img_rect.x = col_count * tile_size
                    img_rect.y = row_count * tile_size
                    tile = (img, img_rect)
                    self.tile_list.append(tile)
                if tile == 2:
                    img = pygame.transform.scale(stone_block_img, (tile_size, tile_size))
                    img_rect = img.get_rect()
                    img_rect.x = col_count * tile_size
                    img_rect.y = row_count * tile_size
                    tile = (img, img_rect)
                    self.tile_list.append(tile)
                if tile == 3:
                    img = pygame.transform.scale(grass_sloope_img, (tile_size, tile_size))
                    img_rect = img.get_rect()
                    img_rect.x = col_count * tile_size
                    img_rect.y = row_count * tile_size
                    tile = (img, img_rect)
                    self.tile_list.append(tile)
                if tile == 4:
                    img = pygame.transform.scale(grass_sloope2_img, (tile_size, tile_size))
                    img_rect = img.get_rect()
                    img_rect.x = col_count * tile_size
                    img_rect.y = row_count * tile_size
                    tile = (img, img_rect)
                    self.tile_list.append(tile)
                if tile == 5:
                    img = pygame.transform.scale(grass_sloope3_img, (tile_size, tile_size))
                    img_rect = img.get_rect()
                    img_rect.x = col_count * tile_size
                    img_rect.y = row_count * tile_size
                    tile = (img, img_rect)
                    self.tile_list.append(tile)
                col_count += 1
            row_count += 1

    def draw(self):
        for tile in self.tile_list:
            window.blit(tile[0], tile[1])
        


