pl_sp = 0

world = World(world_data)
game_loop = True
while game_loop:
    keys = pygame.key.get_pressed()
    for event in pygame.event.get():
        if event.type == pygame.QUIT:
            game_loop = False
    world.draw()
    player.show()
    if pl_sp >= 0:
        pl_sp -= 1
    if keys[pygame.K_d]:
        pl_sp += 1
        player.rect.x += pl_sp
    if keys[pygame.K_a]:
        pl_sp += 1
        player.rect.x -= pl_sp
    if keys[pygame.K_w]:
        pl_sp += 1
        player.rect.y -= pl_sp
    if keys[pygame.K_s]:
        pl_sp += 1
        player.rect.y += pl_sp
    fps.tick(60)
    pygame.display.update()
